DROP TABLE IF EXISTS `events_category`;
CREATE TABLE `events_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(20) NOT NULL,
  `value` int(11) NOT NULL,
  `title_zh-cn` varchar(100) NOT NULL,
  `title_en` varchar(100) NOT NULL,
  `note_zh-cn` varchar(400) NOT NULL,
  `note_en` varchar(511) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of events_category
-- ----------------------------
INSERT INTO `events_category` VALUES ('1', '*', '0', '未来/足迹', 'Future/Footprints', '<strong>搭建桥梁，创建跨界平台<br>创新理念，激发思维模式<br>理解融合，落实前沿合作</strong>', '<strong>A bridge and flatform for interdisciplinary subjects.<br>A innovative idea for inspiring thinking.<br>A fusion of understanding for cutting edge cooperation.</strong>');
INSERT INTO `events_category` VALUES ('2', '.lecture', '2', '理解未来讲座', 'Understanding the Future Lectures', '<strong>让我们沉浸在好奇心不断被激发和满足的过程中，智识版本实时更新</strong><br><br>邀请有洞察力和前瞻性的创新科学家和创业家，介绍现代科技最新研究成果和趋势，以丰富听众对未来的预测和认知，促进对未来的理解与思考，促进基础科学的普及。\r\n', null);
INSERT INTO `events_category` VALUES ('3', '.seminar', '4', '闭门耕', 'Exclusive Seminars', '<strong>对话题深度、条分缕析、互动交流、碰撞激发</strong><br><br>不对外公开，小型而私密，预设的交流和讨论具前瞻性、启发性和思辨性话题。', null);
INSERT INTO `events_category` VALUES ('4', '.special', '1', '特别活动', 'Special Events', '', null);

