-- ----------------------------
-- Records of homepage_slides
-- ----------------------------
INSERT INTO `homepage_slides` VALUES ('1', '1', '未来/回顾', 'Future/Highlights', '精准医学之路的曙光<br>谢晓亮教授主讲第九期理解未来讲座', '精准现代医学之路的一缕曙光，谢晓亮教授主讲第九期理解未来讲座\r\n', '', 'The Dawn of Medical Precision<br>Professor Xie Xiaoliang leading the 9th session of the \"Understanding The Future\" lecture series', 'Modern Medicine\'s Ray of Light, Professor Xie Xiaoliang leading the 9th session of \"Understanding The Future\" lecture series');
INSERT INTO `homepage_slides` VALUES ('3', '3', '未来/预告', 'Future/Notice', '2015年9月12日 北京耶鲁中心 <br>理解未来讲座第十期 外尔准粒子的发现 主讲：丁洪 （中科院物理所）', '在固体材料中的“宇宙”中，亿万个电子通过相互作用能形成一种决定其母体材料性质的“准粒子”，这些准粒子与基本粒子可能遵循相同的物理规律。<br>近年来拓扑材料的快速发展为在固体材料中观测外尔费米子提供了新的思路和途径。在这个报告中丁洪先生将介绍我们是如何发现外尔准粒子。', '', 'September 12th, 2015 Yale Center Beijing <br> Ding Hong (Institute of Physics) leading the 10th session of the \"Understanding The Future\" lecture series ', 'In the \"universe\" of solid materials, billions of electrons interact to form a \"quasi-particle\" that determines the nature of the basic parent particle. These quasi-particles and basic particles follow the same physical laws. <br>In recent years, rapid development of topological materials of Weyl fermions provided a new way of thinking. In this lecture, Mr. Ding Hong introduced how the quasi-particles were found.');
INSERT INTO `homepage_slides` VALUES ('4', '0', '', '', '', '', '', '', '');

