  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of miscs
-- ----------------------------
INSERT INTO `miscs` VALUES ('description', 'description', '', '', 'global', 'input-text', '30');
INSERT INTO `miscs` VALUES ('description-footprint', 'description', '', '', 'footprint', 'input-text', '130');
INSERT INTO `miscs` VALUES ('description-news', 'description', '', '', 'news', 'input-text', '130');
INSERT INTO `miscs` VALUES ('description-people', 'description', '', '', 'people', 'input-text', '130');
INSERT INTO `miscs` VALUES ('footprint-bigtitle', '大标题', '未来/足迹', 'Future/Footprints', 'footprint', 'input-text', '0');
INSERT INTO `miscs` VALUES ('footprint-text', '标题附言', '这是一次科学与高新技术产业的完美握手。未来论坛不仅关注科学的未来，更为科学与产业的连接铺平了道路。在未来论坛，我们可以找到中国经济继续保持健康快速发展的钥匙。<br>在这里，我们能够看到科学之美和科学即将产生的力量。', 'The Future Forum is the perfect crossroads between science and high technology. The forum is not only concerned about the future of science but has also paved the way for a connection between science and everyday life. The Future Forum shows us the key to maintaining a healthy and rapid economic growth of China. Here, we are able to see the beauty and power of science.', 'footprint', 'textarea', '10');
INSERT INTO `miscs` VALUES ('homepage-action-note', '行动-附言', '处其实，不居其华。未来论坛默默耕耘、精心奉献；<br>每月举办的理解未来系列讲座，邀请了蜚声中外的科学家，如张首晟，潘建伟，张双南，谢晓亮，丁洪。<br>每季度举办闭门耕研讨沙龙，深度探求、条分缕析、互动交流、思维碰撞<br>一年一度的未来年会, 是全球科学人文创新的盛宴。', 'The Future Forum hosts a monthly speaker series in Beijing. These talks delve deeply into the lecturer’s area of expertise, and have included luminaries such as Shoucheng Zhang, Jianwei Pan, Shuangnan Zhang, Xiaoliang Xie and Hong Ding. \r\n\r\nThe Future Forum also hosts a quarterly themed seminars which is by-invitation only. This seminar series is focused on proactive, inspiring and critical thinking.\r\n\r\nAs parts of its efforts, the Future Forum hosts an annual conference featuring leading scientists and futurists from around the world.\r\n', 'homepage', 'textarea', '12');
INSERT INTO `miscs` VALUES ('homepage-action-title', '行动-标题', '未来/行动', 'Future/Action', 'homepage', 'input-text', '11');
INSERT INTO `miscs` VALUES ('homepage-footprint-button', '足迹-按钮', '了解更多', 'MORE-', 'homepage', 'input-text', '23');
INSERT INTO `miscs` VALUES ('homepage-footprint-note', '足迹-附言', '这是一次科学与高新技术产业的完美握手。未来论坛不仅关注科学的未来，更为科学与产业的连接铺平了道路。在未来论坛，我们可以找到中国经济继续保持健康快速发展的钥匙。<br>在这里，我们能够看到科学之美和科学即将产生的力量。', 'This is the perfect encounter between science and the new hi-tech industry. The Future Forum is not only focused on the future of science but also has paved the way for the unification of science and industry. We can find the key to the rapid and healthy growth of Chinese economy and witness the beauty and power of science in the Future Forum.', 'homepage', 'textarea', '22');
INSERT INTO `miscs` VALUES ('homepage-footprint-title', '足迹-标题', '未来/足迹', 'Future/Footprints', 'homepage', 'input-text', '21');
INSERT INTO `miscs` VALUES ('homepage-people-button', '人物-按钮', '了解更多', 'MORE', 'homepage', 'input-text', '3');
INSERT INTO `miscs` VALUES ('homepage-people-note', '人物-附言', '中国最具影响力的互联网先行者，投资界成功者、科学界前沿者，同时是具有前沿思考的创业者、创新思维模式的科学家。透过此交流平台，鼓励并支持新生代青年的创业发展及理念。', 'Leaders and influencers aged under 40 from the business, government and academic fields who encourages the young generation’s entrepreneurial ideal.', 'homepage', 'textarea', '2');
INSERT INTO `miscs` VALUES ('homepage-people-title', '人物-标题', '未来/人物', 'Future/Characters', 'homepage', 'input-text', '1');
